// authContext.js
import React, { createContext, useContext, useEffect, useState } from 'react';
import firebase from 'firebase/app';
import 'firebase/auth';
import firebaseConfig from './config.js'; // Import your Firebase configuration

const AuthContext = createContext();

export const useAuth = () => {
  return useContext(AuthContext);
};

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Initialize Firebase with your configuration
    const app = firebase.initializeApp(firebaseConfig);

    // Set up an observer to listen for authentication state changes
    const unsubscribe = firebase.auth().onAuthStateChanged((user) => {
      setCurrentUser(user);
      setLoading(false);
    });

    return () => {
      // Unsubscribe from the observer when the component unmounts
      unsubscribe();
    };
  }, []);

  const value = {
    currentUser,
    // Add signIn and signOut functions here
  };

  return <AuthContext.Provider value={value}>{!loading && children}</AuthContext.Provider>;
};
