    //                                 {(provided) => (
    //                                 <div className='image-container' {...provided.dragHandleProps} {...provided.draggableProps} ref={provided.innerRef}>
    //                                     <img src={store.src}/>
    //                                 </div>)}
    //                             </Draggable>
    //                         ))}
    //                         </div>
    //                     )}               
    //                 </Droppable>
    //             </div>
                
                    {/* <img src={img1} alt='one'/>
                    <img src={img2} alt='two'/>
                    <img src={img3} alt='three'/>
                    <img src={img4} alt='four'/>
                    <img src={img5} alt='five'/>
                    <img src={img6} alt='six'/>
                    <img src={img7} alt='seven'/>
                    <img src={img8} alt='eight'/>
                    <img src={img9} alt='nine'/>
                    <img src={img10} alt='ten'/>
                    <img src={img11} alt='eleven'/>
                    <img src={img12} alt='twelve'/>
                <img src={img13} alt='thirteen'/> */}
    //         </div>
    //     </DragDropContext>
    // </div>
  )
}








    <DragDropContext onDragEnd={handleDragEnd}>
        <Droppable droppableId="image-list" direction="horizontal">
        {(provided) => (
            <ul
            {...provided.droppableProps}
            ref={provided.innerRef}
            className="image-list"
            >
            {images.map((image, index) => (
                <Draggable key={image.id} draggableId={image.id} index={index}>
                {(provided) => (
                    <li
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    >
                    <img src={image.src} alt={`Something ${index + 1}`} />
                    </li>
                )}
                </Draggable>
            ))}
            </ul>
        )}
        </Droppable>
  </DragDropContext>
export default Images;
