import React, { useState, useEffect } from 'react';
import './App.css';
import Hero from './components/Hero';
import Images from './components/Images';
import SignIn from './components/LogIn';
import { useAuth } from './auth';

function App() {
  // Assuming you have an auth context that provides user information
  const { user } = useAuth();

  // State to track whether the user is authorized
  const [isAuthorized, setIsAuthorized] = useState(false);

  // useEffect to check user authorization when the user object changes
  useEffect(() => {
    if (user) {
      // User is authorized, set isAuthorized to true
      setIsAuthorized(true);
    } else {
      // User is not authorized, set isAuthorized to false
      setIsAuthorized(false);
    }
  }, [user]);

  return (
    <div className="App">
      {isAuthorized ? (
        // Render these components when user is authorized
        <>
          <Hero />
          <Images />
        </>
      ) : (
        // Render the SignIn component when user is not authorized
        <SignIn />
      )}
    </div>
  );
}

export default App;
