import React, { createContext, useContext, useEffect, useState } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, onAuthStateChanged } from 'firebase/auth';
import { signInWithEmailAndPassword, signOut } from 'firebase/auth';
import firebaseConfig from './config.js'; // Import your Firebase configuration

const AuthContext = createContext();

export const useAuth = () => {
  return useContext(AuthContext);
};

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Initialize Firebase with your configuration
    const app = initializeApp(firebaseConfig);

    // Get the authentication instance
    const auth = getAuth(app);

    // Set up an observer to listen for authentication state changes
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      setCurrentUser(user);
      setLoading(false);
    });

    return () => {
      // Unsubscribe from the observer when the component unmounts
      unsubscribe();
    };
  }, []);

  const signIn = async (email, password) => {
    try {
      const app = initializeApp(firebaseConfig);
      const auth = getAuth(app);
      await signInWithEmailAndPassword(auth, email, password);
    } catch (error) {
      // Handle sign-in error
      console.error('Error signing in:', error);
    }
  };

  const signOutUser = async () => {
    try {
      const app = initializeApp(firebaseConfig);
      const auth = getAuth(app);
      await signOut(auth);
    } catch (error) {
      // Handle sign-out error
      console.error('Error signing out:', error);
    }
  };

  const value = {
    currentUser,
    signIn, // Include the signIn function
    signOut: signOutUser, // Include the signOut function
  };

  return <AuthContext.Provider value={value}>{!loading && children}</AuthContext.Provider>;
};
