  return (
            
    <div>
        <DragDropContext onDragEnd={() => {
                console.log('drag');
            }}>
            <div className='imgContainer'>    
                <div className='imgGallery'>
                    <Droppable droppableId='root' type='group'>
                        {(provided) => (
                            <div {...provided.droppableProps} ref={provided.innerRef}>
                                {images.map(images, index => (
                                <Draggable draggableId={images.id} key={images.id} index={index}>
                                    {(provided) => (
                                    <div className='image-container' {...provided.dragHandleProps} {...provided.draggableProps} ref={provided.innerRef}>
                                        <img src={images.src}/>
                                    </div>)}
                                </Draggable>
                            ))}
                            </div>
                        )}               
                    </Droppable>
                </div>
                
                    {/* <img src={img1} alt='one'/>
