import React from 'react'
import './Image.css';
import { useEffect, useState } from "react";
import img1 from '../img/pexels_1.jpg';
import img2 from '../img/pexels_2.jpg';
import img3 from '../img/pexels_3.jpg';
import img4 from '../img/pexels_4.jpg';
import img5 from '../img/pexels_5.jpg';
import img6 from '../img/pexels_6.jpg';
import img7 from '../img/pexels_7.jpg';
import img8 from '../img/pexels_8.jpg';
import img9 from '../img/pexels_9.jpg';
import img10 from '../img/pexels_10.jpg';
import img11 from '../img/pexels_11.jpg';
import img12 from '../img/pexels_12.jpg';
import img13 from '../img/pexels_13.jpg';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

function Images({ filteredImg, searchTerm }) {

    const initialImages = [
        { id: 'image1', src: img1, tags: ['stuff', 'landscape'] },
        { id: 'image2', src: img2, tags: ['stuff', 'landscape'] },
        { id: 'image3', src: img3, tags: ['stuff', 'landscape'] },
        { id: 'image4', src: img4, tags: ['stuff', 'landscape'] },
        { id: 'image5', src: img5, tags: ['stuff', 'landscape'] },
        { id: 'image6', src: img6, tags: ['stuff', 'landscape'] },
        { id: 'image7', src: img7, tags: ['nature', 'things'] },
        { id: 'image8', src: img8, tags: ['nature', 'things'] },
        { id: 'image9', src: img9, tags: ['nature', 'things'] },
        { id: 'image10', src: img10, tags: ['nature', 'things'] },
        { id: 'image11', src: img11, tags: ['nature', 'landscape'] },
        { id: 'image12', src: img12, tags: ['nature', 'landscape'] },
        { id: 'image13', src: img13, tags: ['nature', 'landscape'] },
        
      ];
      
      const [images, setImages] = useState(initialImages);




      const handleDragEnd = (result) => {
        if (!result.destination) {
          return;
        }
      
        const updatedImages = [...images];
        const [reorderedImage] = updatedImages.splice(result.source.index, 1);
        updatedImages.splice(result.destination.index, 0, reorderedImage);
      
        setImages(updatedImages);
      };



       const [loading, setLoading] = useState(true);

  // Your image data and other necessary state variables
//   const [images, setImages] = useState([]); // Add your image data here

  useEffect(() => {
    // Simulate an asynchronous image loading process
    setTimeout(() => {
      // Once images are loaded (you can replace this with your actual image loading logic), set loading to false
      setLoading(false);
    }, 2000); // Adjust the time to simulate loading as needed
  }, []);




   // Filter images based on the search term
   const filteredImages = filteredImg.filter((image) =>
   image.tags.some((tag) => tag.toLowerCase().includes(searchTerm.toLowerCase()))
 );





  return (
    <div className="image-gallery">
      {loading ? (
        // Render skeleton loader or loading spinner while images are loading
        <div className="loader">Loading...</div>
      ) : (

          
        <div className='imgContainer'>
        {filteredImages.map((image) => (
        <DragDropContext onDragEnd={handleDragEnd}>
        <Droppable droppableId="image-list" direction="horizontal">
        {(provided) => (
            <div
            {...provided.droppableProps}
            ref={provided.innerRef}
            className="image-list"
            >
            {images.map((image, index) => (
                <Draggable key={image.id} draggableId={image.id} index={index}>
                {(provided) => (
                    <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    className='listed'
                    >
                    <img src={image.src} alt={`Index: ${index + 1}`} />
                    </div>
                )}
                </Draggable>
            ))}
            </div>
        )}
        </Droppable>
    </DragDropContext>
    ))}
        </div>
    )}
    </div>

  )
}

export default Images;
